from math import log2, powA4 = 440C0 = A4 * pow(2, -4.75)name = ["C", "C#", "D", "D#", "E", "F", "F#", "G", "G#", "A", "A#", "B"]def pitch(freq):    h = round(12 * log2(freq / C0))    octave = h // 12    n = h % 12    return name[n] + str(octave)def note_to_freq_range(note):    octave = int(note[-1])    note_index = name.index(note[:-1])    note_num = note_index + 12 * octave    low_note = note_num - 0.5    high_note = note_num + 0.5    freq_low = C0 * 2 ** (low_note / 12)    freq_high = C0 * 2 ** (high_note / 12)    return freq_low, freq_high